import React, { Component } from "react";
import PropTypes from "prop-types";
import classNames from "classnames";
import { Container, Navbar } from "shards-react";

import NavbarSearch from "./NavbarSearch";
import NavbarNav from "./NavbarNav/NavbarNav";
import NavbarToggle from "./NavbarToggle";
import './../../../style/scrollerdesign.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {
  faPlus, faTimes,

} from '@fortawesome/free-solid-svg-icons'
import {
  FloatingMenu,
  MainButton,
  ChildButton,
} from 'react-floating-button-menu';

class MainNavbar extends Component {

  state = {
    isOpen: false,
  }

  render() {
    const classes = classNames(
      "main-navbar",
      "bg-white",
      this.props.stickyTop && "sticky-top"
    );
    const isOpen = this.state.isOpen

    const buttonSlide = this.props.open ?
      <div onClick={this.props.openSideBar} className="button_cont"
        align="center">
        <b className="example_e" href="#" target="_blank" rel="nofollow noopener">
          Menu agTEK
      </b></div> : <b></b>

    return (
      <div className={classes}>
        <Container className="p-0">


          <Navbar type="light" className=" flex-md-nowrap p-0">
            <div className="main-navbar__search w-100 d-none d-md-flex d-lg-flex">
              <div seamless className="ml-3">

                {buttonSlide}

                <center>

            </center> 
              </div>
              <div className="d-table m-auto">
              <img
                id="main-logo"
                className="d-inline-block align-top mr-1"
                style={{ maxWidth: "90px" }}
                src={require("../../../images/sygnet-agtek.png")}
                alt="Shards Dashboard"
              />
            </div>
            </div>

            <NavbarNav />
            <NavbarToggle />
          </Navbar>
        </Container>
      </div>
    );
  };
}


MainNavbar.propTypes = {
  /**
   * The layout type where the MainNavbar is used.
   */
  layout: PropTypes.string,
  /**
   * Whether the main navbar is sticky to the top, or not.
   */
  stickyTop: PropTypes.bool
};

MainNavbar.defaultProps = {
  stickyTop: true
};

export default MainNavbar;
