import React, { Component } from 'react';
import { Container, Row, Col } from 'shards-react';
import PageTitle from './../../components/common/PageTitle';
import SmallStats from './../../components/common/SmallStats';
import UsersOverview from './statistique/UsersOverview';
import UsersByDevice from './statistique/UsersByDevice';
class Main extends Component {
  state = {
    visible: true,
    smallStats: [
      {
        label: 'Production Lait',
        value: '2,390',
        percentage: '4.7%',
        increase: true,
        chartLabels: [null, null, null, null, null, null, null],
        attrs: { md: '6', sm: '6' },
        datasets: [
          {
            label: 'Today',
            fill: 'start',
            borderWidth: 1.5,
            backgroundColor: 'rgba(0, 184, 216, 0.1)',
            borderColor: 'rgb(0, 184, 216)',
            data: [1, 2, 1, 3, 5, 4, 7]
          }
        ]
      },
      {
        label: 'Moyen Poid',
        value: '182',
        percentage: '12.4',
        increase: true,
        chartLabels: [null, null, null, null, null, null, null],
        attrs: { md: '6', sm: '6' },
        datasets: [
          {
            label: 'Today',
            fill: 'start',
            borderWidth: 1.5,
            backgroundColor: 'rgba(23,198,113,0.1)',
            borderColor: 'rgb(23,198,113)',
            data: [1, 2, 3, 3, 3, 4, 4]
          }
        ]
      },
      {
        label: 'le Gain',
        value: '8,147',
        percentage: '3.8%',
        increase: false,
        decrease: true,
        chartLabels: [null, null, null, null, null, null, null],
        attrs: { md: '4', sm: '6' },
        datasets: [
          {
            label: 'Today',
            fill: 'start',
            borderWidth: 1.5,
            backgroundColor: 'rgba(255,180,0,0.1)',
            borderColor: 'rgb(255,180,0)',
            data: [2, 3, 3, 3, 4, 3, 3]
          }
        ]
      },
      {
        label: 'new vaches',
        value: '29',
        percentage: '2.71%',
        increase: false,
        decrease: true,
        chartLabels: [null, null, null, null, null, null, null],
        attrs: { md: '4', sm: '6' },
        datasets: [
          {
            label: 'Today',
            fill: 'start',
            borderWidth: 1.5,
            backgroundColor: 'rgba(255,65,105,0.1)',
            borderColor: 'rgb(255,65,105)',
            data: [1, 7, 1, 3, 1, 4, 8]
          }
        ]
      },
      {
        label: 'GAIN TOTAL',
        value: '17,281',
        percentage: '2.4%',
        increase: true,
        decrease: false,
        chartLabels: [null, null, null, null, null, null, null],
        attrs: { md: '4', sm: '6' },
        datasets: [
          {
            label: 'Today',
            fill: 'start',
            borderWidth: 1.5,
            backgroundColor: 'rgb(0,123,255,0.1)',
            borderColor: 'rgb(0,123,255)',
            data: [3, 2, 3, 2, 4, 5, 4]
          }
        ]
      }
    ]
  };

  dismiss = () => {
    this.setState({ visible: false });
  };

  render() {
    const smallStats = this.state.smallStats;
    return (
      <Container fluid className="main-content-container px-4">
        <Row noGutters className="page-header py-4">
          <PageTitle
            title="Statistique"
            subtitle="agTEK"
            className="text-sm-left mb-3"
          />
        </Row>
        <Row>
          {smallStats.map((stats, idx) => (
            <Col className="col-lg mb-4" key={idx} {...stats.attrs}>
              <SmallStats
                id={`small-stats-${idx}`}
                variation="1"
                chartData={stats.datasets}
                chartLabels={stats.chartLabels}
                label={stats.label}
                value={stats.value}
                percentage={stats.percentage}
                increase={stats.increase}
                decrease={stats.decrease}
              />
            </Col>
          ))}
        </Row>
        <Row>
          {/* Users Overview */}
          <Col lg="8" md="12" sm="6" className="mb-2">
            <UsersOverview />
          </Col>

          {/* Users by Device */}
          <Col lg="4" md="6" sm="6" className="mb-2">
            <UsersByDevice />
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Main;
