/* eslint-disable import/no-extraneous-dependencies */
/* eslint-disable no-unused-vars */
/* eslint-disable react/destructuring-assignment */
import React, { Component, Fragment } from 'react';
import { FormInput, ButtonGroup, Button, FormSelect } from 'shards-react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faWrench,
  faExclamationTriangle,
  faTimesCircle,
  faCheckCircle,
  faCheck,
  faRedo,
  faPencilAlt
} from '@fortawesome/free-solid-svg-icons';
import SlidingPane from 'react-sliding-pane';
import Modal from 'react-modal';
import 'react-sliding-pane/dist/react-sliding-pane.css';
import '../../mainPage/style.css';
import '../../../style/scrollerdesign.css';
import '../../../style/scrollerdesign2.css';

class USERRow2 extends Component {
  state = {
    edit: false,
    USER: [],
    USEREdit: false,
    change: {}
  };

  componentDidMount() {
    Modal.setAppElement('body');
  }

  showEditCow = () => {

    this.setState({
      USEREdit: true
    });
  };

  renderActions = () => {
    return (
      <ButtonGroup>
        <Button outline pill theme="success" onClick={() => this.editing()}>
          <FontAwesomeIcon size="lg" icon={faPencilAlt} />
        </Button>
        <Button
          outline
          pill
          theme="danger"
          onClick={() => {
            this.props.delete(this.props.index);
          }}
        >
          <FontAwesomeIcon size="lg" icon={faTrash} />
        </Button>
        <SlidingPane
          className="some-custom-class up"
          overlayClassName="some-custom-overlay-class"
          title="Fermer"
          subtitle="Fermer ajout de vache."
          width="50%"
          isOpen={this.state.USEREdit}
          onRequestClose={() => {
            // triggered on "<" on left top click or on outside click
            this.setState({ USEREdit: false, NameFooderUSER: [], Fooder: [] });
          }}
        >
          <Row form>
            <Col md="6" className="form-group">
              <label htmlFor="USERName">nom USER</label>
              <FormInput
                valid={this.state.invalidname}
                invalid={this.state.validname}
                id="USERName"
                name="USERName"
                type="text"
                placeholder="nom du USER .."
                onChange={this.handleChange}
                defaultValue={this.props.detail.USERName}
              />
            </Col>
            <Col md="6" className="form-group">
              <label htmlFor="DateCow">etat </label>
              {USERStateListe}
            </Col>
          </Row>
          <Row form>
            <Col md="6" className="form-group">
              <label htmlFor="USERName">nom USER</label>
              <FormInput
                valid={this.state.invalidname}
                invalid={this.state.validname}
                id="USERName"
                name="USERName"
                type="text"
                placeholder="nom du USER .."
                onChange={this.handleChange}
                defaultValue={this.props.detail.USERName}
              />
            </Col>
            <Col md="6" className="form-group">
              <label htmlFor="DateCow">etat </label>
              {USERStateListe}
            </Col>
          </Row>

          <br />
          <br />
          <Row form>
            <Col />
            <Col className="form-group">
              <center>
                <Button
                  outline
                  pill
                  theme="danger"
                  onClick={() => {
                    this.setState({
                      USEREdit: false
                    });
                  }}
                >
                  <FontAwesomeIcon size="lg" icon={faRedo} />
                </Button>
                &nbsp; &nbsp; &nbsp; &nbsp;
                <Button
                  outline
                  pill
                  theme="primary"
                  onClick={e => this.onEdit(e)}
                >
                  <FontAwesomeIcon size="lg" icon={faCheck} />
                </Button>
              </center>
            </Col>
          </Row>
        </SlidingPane>
      </ButtonGroup>
    );
  };
  editing = () => {
    let { edit, USER } = this.state;
    console.log(this.state);
    this.setState({
      edit: !edit,
      USER
    });
    console.log({ edit });
  };
  handleChange = e => {
    let { edit, USER, change } = this.state;
    if (e.target.id == 'Role') {
      change[e.target.id] = parseInt(e.target.value);
    } else change[e.target.id] = e.target.value;

    this.setState({
      edit: edit,
      USER: USER,
      change: change
    });
    console.log(this.state);
  };
  onEdit = e => {
    e.preventDefault();

    let change = this.state.change;
    console.log('change :');
    console.log(change);
    this.props.edit(this.props.index, change);
    this.editing();
  };
  anuuler = e => {
    e.preventDefault();
    this.editing();
  };
  renderUpdate = () => {
    return (
      <tr>
        <td className="td2">
          <FormInput
            type="text"
            onChange={this.handleChange}
            ref="USERName"
            defaultValue={this.props.detail.USERName}
            id="USERName"
          />
        </td>

        <td className="td2">
          <FormSelect
            id="Role"
            defaultValue={this.props.detail.Role}
            onChange={this.handleChange}
          >
            <option value="0" selected>
              Admin
            </option>

            <option value="1">user</option>
            <option value="2">super user</option>
          </FormSelect>
        </td>

        <td className="td2">
          {' '}
          <ButtonGroup size="sm">
            <Button outline pill theme="primary" onClick={this.onEdit}>
              <i className="material-icons bg">done</i>
            </Button>
            <Button outline pill theme="danger" onClick={this.anuuler}>
              <i className="material-icons bg">close</i>
            </Button>
          </ButtonGroup>
        </td>
      </tr>
    );
  };
  render() {
    let { edit } = this.state;

    return (
      <Fragment>{edit ? this.renderUpdate() : this.renderActions()}</Fragment>
    );
  }
}

export default USERRow2;
