import React, { Component } from 'react';
import {
    ListGroup,
    ListGroupItem,
    Row,
    Col,
    Form,
    FormInput,
    FormSelect,
    Button
} from "shards-react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { Config } from './../../../configue';
import {
     faCheck, faRedo

} from '@fortawesome/free-solid-svg-icons'
class USERForm extends Component {

    state = {

        idUSER: 0,
        USERName: "",
        Role: 0
    }
    handleChange = (e) => {
        if (e.target.id === "Role") {
            this.setState({
                [e.target.id]: parseInt(e.target.value),
            })
        } else {
            this.setState({
                [e.target.id]: e.target.value,
            })
        }



    }

    postMethode(a) {
        var encodedKeyUSERName = encodeURIComponent("USERName")
        var encodedKeyRole = encodeURIComponent("Role")


        var encodedvalUSERName = encodeURIComponent(a.USERName);
        var encodedvalRole = encodeURIComponent(parseInt(a.Role));

        var formbody = [
            encodedKeyUSERName + '=' + encodedvalUSERName,
            encodedKeyRole + '=' + encodedvalRole,

        ];
        formbody = formbody.join("&");

        const data = fetch(Config.url + "/USER", {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/x-www-form-urlencoded', // <-- Specifying the Content-Type
            },
            body: formbody // <-- Post parameters
        })
            .then((responseText) => {
                return (responseText.json());
            })
            .catch((error) => {
                console.error(error);
            });
        return data
    }
    handleSubmit = (e) => {
        e.preventDefault(e)


        this.postMethode(this.state).then((d) => {
            var idUSER = d.data

            this.props.addUSER(this.state, idUSER)
            this.setState({

                idUSER: 0,
                USERName: "",
                Role: 0
            })
        })


    }
    render() {


        return (<ListGroup flush>
            <ListGroupItem className="p-3">
                <Row>
                    <Col>
                        <Form onSubmit={this.handleSubmit}>
                            <Row form>
                                <Col md="6" className="form-group">
                                    <label htmlFor="USERName">nom USER</label>
                                    <FormInput
                                        id="USERName"
                                        name="USERName"
                                        type="text"
                                        placeholder="Code du USER .."
                                        onChange={this.handleChange}
                                        value={this.state.USERName}
                                    />
                                </Col>



                                <Col md="6" className="form-group">
                                    <label htmlFor="Role">Role</label>
                                    <FormSelect id="Role" onChange={this.handleChange} >
                                        <option value="0" selected>Admin</option>

                                        <option value="1" >user</option>
                                        <option value="2">super user</option>

                                    </FormSelect>
                                </Col>
                            </Row>
                            <Row>
                                <Col md="6" className="form-group">
                                    <center>
                                        <Button outline pill theme="danger"
                                            onClick={() => { this.setState({ USERName: "" }) }} ><FontAwesomeIcon size="lg" icon={faRedo} /></Button>
                                    </center>
                                </Col>
                                <Col md="6" className="form-group">
                                    <center>
                                        <Button outline pill theme="primary" type="submit"
                                        ><FontAwesomeIcon size="lg" icon={faCheck} /></Button>
                                    </center>
                                </Col>




                            </Row>
                        </Form>
                    </Col>

                </Row>

            </ListGroupItem>
            <ListGroupItem>
                <Row>

                </Row>
            </ListGroupItem>
        </ListGroup>
        );
    }
}


export default USERForm;
