import React, { Component, Fragment } from 'react';
import { ButtonGroup, Button } from 'shards-react';

class Rows extends Component {
  state = {
    edit: false,
    animaux: { id: '', type: '', mode: '' }
  };

  renderActions = () => {
    return (
      <tr>
        <td>{this.props.detail.id}</td>
        <td>{this.props.detail.type}</td>
        <td>{this.props.detail.mode}</td>
        <td>
          <Button onClick={() => this.editing()} theme="white">
            <span className="text-success">
              <i className="material-icons">check</i>
            </span>{' '}
            Approve
          </Button>

          <Button
            onClick={() => {
              this.props.delete(this.props.index);
            }}
            theme="white"
          >
            <span className="text-danger">
              <i className="material-icons">clear</i>
            </span>{' '}
            Reject
          </Button>
          <Button onClick={() => this.editing()} theme="white">
            <span className="text-light">
              <i className="material-icons">more_vert</i>
            </span>{' '}
            Edit
          </Button>
        </td>
      </tr>
    );
  };
  editing = () => {
    let { edit, animaux } = this.state;
    console.log(this.state);
    this.setState({
      edit: !edit,
      animaux
    });
    console.log({ edit });
  };
  handleChange = e => {
    let { edit } = this.state;
    console.log(e.target.value);

    this.setState({ edit, animaux: { [e.target.id]: e.target.value } });
    console.log('ba3ed edit');
    console.log(this.state);
  };
  onEdit = e => {
    e.preventDefault();
    console.log('ref');
    console.log(this.refs.type.value);
    console.log(this.refs.mode.value);
    let type = this.refs.type.value;
    let mode = this.refs.mode.value;
    this.props.edit(this.props.index, type, mode);
    this.editing();
  };

  renderUpdate = () => {
    return (
      <tr>
        <td scope="col" className="border-0">
          {' '}
          {this.props.detail.id}
        </td>
        <td scope="col" className="border-0">
          {' '}
          <input
            type="text"
            ref="type"
            defaultValue={this.props.detail.type}
            id="type"
          />
        </td>
        <td scope="col" className="border-0">
          {' '}
          <input
            type="text"
            ref="mode"
            defaultValue={this.props.detail.mode}
            id="mode"
          />
        </td>
        <td scope="col" className="border-0">
          {' '}
          <ButtonGroup size="sm">
            <Button onClick={this.onEdit} theme="white">
              <span className="text-success">
                <i className="material-icons">check</i>
              </span>{' '}
              Edit
            </Button>
          </ButtonGroup>
        </td>
      </tr>
    );
  };

  render() {
    let { edit } = this.state;
    return (
      <Fragment>{edit ? this.renderUpdate() : this.renderActions()}</Fragment>
    );
  }
}

export default Rows;
